import { useAuth0 } from "@auth0/auth0-react";
import React, { useEffect, useState } from "react";
import { CodeSnippet } from "../components/code-snippet";
import Table from "../components/table";
import { PageLayout } from "../components/page-layout";
import { getAdminResource } from "../services/message.service";


export const AdminPage: React.FC = () => {
  const [message, setMessage] = useState<string>("");

  const { getAccessTokenSilently } = useAuth0();

  useEffect(() => {
    let isMounted = true;

    const getMessage = async () => {
      const accessToken = await getAccessTokenSilently();
      const { data, error } = await getAdminResource(accessToken);

      if (!isMounted) {
        return;
      }

      if (data) {
        setMessage(JSON.stringify(data, null, 2));
      }

      if (error) {
        setMessage(JSON.stringify(error, null, 2));
      }
    };

    getMessage();

    return () => {
      isMounted = false;
    };
  }, [getAccessTokenSilently]);

  let jsonData = JSON.parse(message).report.actions;
  console.log(jsonData);

  return (
    <PageLayout>
      <div className="content-layout">
        <h1 id="page-title" className="content__title">
          Report only for 'Manager'
        </h1>
        <div className="content__body">
          <p id="page-description">
            <span>
              This page retrieves the <strong>"action - action bound trigger - application report"</strong> from 
              manage API.
            </span>
            <span>
              <strong>
                Only authenticated users with the{" "}
                <code>read:admin-messages</code> permission should access this
                page.
              </strong>
            </span>
          </p>
          <CodeSnippet title="Status" code={message.includes("Permission denied")?"Permission Denied!":"You are manager and can access the report!"} />
          <p/>
          <p>Report</p>
          <table>
            <thead>
                <tr>
                <th>Application</th>
                <th>Action</th>
                <th>Trigger</th>
                <th>Status</th>
                </tr>
            </thead>
            <tbody>
              {jsonData.map(item => (
                <tr key={item.id}>
                  <td>item.app</td>
                  <td>{item.name}</td>
                  <td>{item.supported_triggers}</td>
                  <td>{item.all_changes_deployed}</td>
                </tr>
              ))}
            </tbody>


            </table>

        </div>
      </div>
    </PageLayout>
  );
};
